import React,{useState} from "react";

const App = () =>{
    const [name, setName] = useState(null);

    const handleClick = () =>{
        return(
        <p>이름 : {name}</p>
        );
    }

    const handleChange = (e) =>{
        setName(e.target.value)
    }

    return(
        <>
           <input type="text" onChange={handleChange}/>
           <button onClick={handleClick}>Upload</button> 
        </>
    )
}

export default App;
