import styles from "./FirstModule.module.css";
import { useState } from "react";

const FirstModule = () => {
  const [isHoverd,setisHoverd] = userState("false");

  return (
    <div className={`${styles.wrapper} ${styles.h1&&isHoverd}`} onClick={()=>
      {
         setisHoverd("true");
      }
    }>
      <h1>Hello, React!</h1>
    </div>
  );
};

export default FirstModule;
