const FirstModule = () => {
  let isHoverd = false;

  return (
    <div className={`${styles.wrapper} ${styles.h1&&isHoverd}`} onClick={()=>
      {
         isHoverd =true;
      }
    }>
      <h1>Hello, React!</h1>
    </div>
  );
};

export default FirstModule;
