import React,{useState} from "react";

const App = () =>{
    const [count, setCount] = useState(0);

    const handleCountUpdate = () =>{
        if(count < 3){
        setCount(count+1);
        }
    }

    return(
        <>
            <button onClick={handleCountUpdate}>Update</button>
            <span>count : {count}</span>
        </>
    )
}

export default App;
