const Button = ({onclick,message})=>{
    return(
      <button onClick={onclick}>{message}</button>  
    );
}


const PlayButton = ({ musicName }) => {
    const handlePlayClick = () => {
      alert(`${musicName}을 재생합니다.`);
    };
  
    return <Button onClick={handlePlayClick}>▶ Play "{musicName}"</Button>;
};

const StopButton = ()=>{
    return <Button onClick={()=>alert("재생을 종료합니다.")}>Stop</Button>
}


const ControlPanel = () =>{
    return(
        <>
        <PlayButton musicName="Way 4 luv"/>
        <StopButton/>
        </>
    );
}

export default ControlPanel;
