import { useState } from "react";

const Counter = () => {
  const [count, setCount] = useState(0);

  const incrementCount = () => {
      setCount(count + 1);
  };

  const manyIncrementCount = () => {
    incrementCount();
    incrementCount();
    incrementCount();
  };

  return (
    <div>
      <h1>Count 값 : {count}</h1>
      <button onClick={manyIncrementCount}>3씩 증가</button>
    </div>
  );
};

export default Counter;
