import styles from "./FirstModule.module.css";
import { useState } from "react";

const FirstModule = () => {
  const [isHoverd, setHoverd] = useState(true);

  return (
    <div className={`${styles.wrapper} ${styles.h1&&isHoverd}`}>
      <h1 onClick={()=>
        setHoverd(0)
      }>Hello, React!</h1>
    </div>
  );
};

export default FirstModule;
